// Write a program to display total Marks and average of 6 subjects.

package com.jyotsna.basic;

public class Average {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 20;
		int number1 = 30; 
		int number2 = 40;
		int number3 = 50;
		int number4 = 60;
		int number5 = 70;
		int sum = number+number1+number2+number3+number4+number5;
		int average = sum/6;
		System.out.println("the value of integer number is :"+number);
		System.out.println("the value of integer number1 is :"+number1);
		System.out.println("the value of integer number2 is :"+number2);
		System.out.println("the value of intger number3 is :"+number3);
		System.out.println("the value of integer number4 is :"+number4);
		System.out.println("the value of integer number5 is :"+number5);
		System.out.println("the value of integer sum is :"+sum);
        System.out.println("the value of integer average is :"+average);



	}

}
