//Write a program to print the area and perimeter of a circle

package com.jyotsna.basic;

public class Circle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float pie = 3.14f;
		float radius = 2.1f;
		float area = pie*radius*radius;
	    float perimeter  = 2*pie*radius;
		System.out.println("the value of area is :"+area);
		System.out.println("the value of perimeter is :"+perimeter);


	}

}
