package com.jyotsna.basic;

public class Displayvalue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int number=467;
float number1 = 34.45f;
double number2 = 324.6;
char alphabate1 = 'w';
char alphabate2 = 'e';
System.out.println("the value of integer number is :"+number);
System.out.println("the value of float number1 is :"+number1);
System.out.println("the value of double number is :"+number2);
System.out.println("the value of character alphabate1 is :"+alphabate1);
System.out.println("the value of character alphabate2 is :"+alphabate2);
	}

}
