package patterens;

public class Pattern4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int row;
		for(row=5;row>=1;row--)
		{
			for(int col=1;col<=row;col++)
			{
				
				System.out.print(col);
				
			}
			System.out.println();
			}

	}

}
